import React, { useState, useEffect, useRef } from "react";
import {
  Card,
  CardContent,
  Typography,
  FormControl,
  Radio,
  RadioGroup,
  FormControlLabel,
  Button,
  Box,
  Alert,
  TextField,
} from "@mui/material";

const Question = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState("");
  const [selected, setSelected] = useState("");
  const [timer, setTimer] = useState(60); 
  const [activeQuestion, setActiveQuestion] = useState(0);
  const [answers, setAnswers] = useState([]); 
  const ws = useRef(null);
  const [scoreboard, setScoreboard] = useState([]);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [question, setQuestion] = useState(null);
  const [choices, setChoices] = useState([]);
  const [selectedAnswer, setSelectedAnswer] = useState("");


  useEffect(() => {
    ws.current = new WebSocket("ws://localhost:3080");

    ws.current.onmessage = (message) => {
      const incomingData = JSON.parse(message.data);
      if (incomingData.type === "newQuestion") {
        setData(incomingData);
        setSelected(""); 
      }
    };

    return () => {
      if (ws.current) ws.current.close();
    };
  }, []);

  
  useEffect(() => {
    if (timer > 0) {
      const countdown = setTimeout(() => setTimer(timer - 1), 2000);
      return () => clearTimeout(countdown);
    }
  }, [timer]);

  const handlePrev = () => {
    setError("");
    if (activeQuestion > 0) {
      setActiveQuestion(activeQuestion - 1);
    }
  };

  const handleNext = () => {
    if (selected === "") {
      return setError("Please select one option!");
    }

    
    const updatedAnswers = [...answers];
    updatedAnswers[activeQuestion] = {
      q: data.question,
      a: selected,
    };
    setAnswers(updatedAnswers);

    setActiveQuestion(activeQuestion + 1);
    setSelected("");
  };

  
  const handleSubmit = () => {
    if (selected === "") {
      return setError("Please select one option!");
    }

   
    const updatedAnswers = [...answers];
    updatedAnswers[activeQuestion] = {
      q: data.question,
      a: selected,
    };
    setAnswers(updatedAnswers);

   
    console.log("Quiz submitted:", updatedAnswers);
  };

  const changeHandler = (e) => {
    setSelected(e.target.value);
    if (error) {
      setError("");
    }
  };
  socket.onmessage = (event) => {
    const data = JSON.parse(event.data);
    
    switch (data.type) {
      case "newQuestion":
        setQuestion(data.question);
        setChoices(data.choices);
        setSelectedAnswer("");
        break;
      case "scoreUpdate":
        setScoreboard(data.scoreboard);
        break;
      case "newMessage":
        setMessages((prevMessages) => [...prevMessages, { user: data.user, message: data.message }]);
        break;
      case "endQuiz":
        setQuestion(data.message);
        setChoices([]);
        break;
      default:
        console.log("Unknown message type:", data.type);
    }
  };

  const handleChatSubmit = () => {
    if (message && ws) {
      ws.send(JSON.stringify({ type: "chatMessage", user: username, message }));
      setMessage("");
    }
  };
  return (
    <Card sx={{ maxWidth: 600, margin: "auto", mt: 4, p: 2 }}>
      <CardContent>
        <Box display="flex" justifyContent="space-between" alignItems="center">
          <Typography variant="h6">
            Question {activeQuestion + 1}/{data?.totalQuestions || "?"}
          </Typography>
          <Typography variant="h6">{timer}s</Typography>
        </Box>
        <Typography variant="h5" sx={{ mt: 2 }}>
          {data?.question || "Loading question..."}
        </Typography>

        {error && <Alert severity="error">{error}</Alert>}

        <FormControl component="fieldset" sx={{ mt: 2 }}>
          <RadioGroup value={selected} onChange={changeHandler}>
            {data?.choices?.map((choice, i) => (
              <FormControlLabel
                key={i}
                value={choice}
                control={<Radio />}
                label={choice}
              />
            ))}
          </RadioGroup>
        </FormControl>
      </CardContent>

      <Box display="flex" justifyContent="space-between" sx={{ p: 2 }}>
        {activeQuestion > 0 && (
          <Button variant="contained" color="secondary" onClick={handlePrev}>
            Prev
          </Button>
        )}
        {activeQuestion + 1 >= data?.totalQuestions ? (
          <Button variant="contained" color="primary" onClick={handleSubmit}>
            Submit
          </Button>
        ) : (
          <Button variant="contained" color="primary" onClick={handleNext}>
            Next
          </Button>
        )}
         <TextField
            label="Type a message"
            variant="outlined"
            fullWidth
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            sx={{ mt: 2 }}
          />
          <Button 
            variant="contained" 
            color="secondary" 
            onClick={handleChatSubmit} 
            sx={{ mt: 2 }}
          >
            Send Message
          </Button>
      </Box>
    </Card>
  );
};

export default Question;
