import React, { useState, useEffect } from "react";
import { Typography, Button, Box, TextField, List, ListItem, Divider } from "@mui/material";
import { useNavigate } from "react-router-dom";

const Quiz = () => {
  const navigate = useNavigate();
  const [chatMessages, setChatMessages] = useState([]);
  const [scoreboard, setScoreboard] = useState([]);
  const [quizQuestion, setQuizQuestion] = useState("");
  const [userAnswer, setUserAnswer] = useState("");
  const [ws, setWs] = useState(null);

  // Establish WebSocket connection
  useEffect(() => {
    const socket = new WebSocket("ws://localhost:8080");
    setWs(socket);

    socket.onopen = () => {
      console.log("Connected to WebSocket");
    };

    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);

      // Handle incoming messages based on type
      switch (data.type) {
        case "newQuestion":
          setQuizQuestion(data.question);
          break;
        case "scoreUpdate":
          setScoreboard(data.scoreboard);
          break;
        case "newMessage":
          setChatMessages((prevMessages) => [...prevMessages, { user: data.user, message: data.message }]);
          break;
        default:
          console.log("Unknown WebSocket message type:", data.type);
      }
    };

    socket.onclose = () => {
      console.log("Disconnected from WebSocket");
    };

    return () => socket.close();
  }, []);

  const handleAnswerSubmit = () => {
    // Send answer to server
    if (ws && userAnswer.trim()) {
      ws.send(JSON.stringify({ type: "submitAnswer", answer: userAnswer }));
      setUserAnswer(""); // Reset answer input
    }
  };

  const endQuiz = () => {
    navigate("/"); // Navigate to home or results page after quiz completion
  };

  const sendMessage = (message) => {
    // Send chat message to server
    if (ws && message.trim()) {
      ws.send(JSON.stringify({ type: "chatMessage", message }));
      setChatMessages((prevMessages) => [...prevMessages, { user: "You", message }]);
    }
  };

  return (
    <Box 
      display="flex" 
      flexDirection="column" 
      alignItems="center" 
      height="100vh" 
      p={3}
      sx={{
        backgroundColor: "#f5f5f5",
        overflowY: "scroll",
      }}
    >
      <Typography variant="h4" gutterBottom>
        Quiz en Mode Multijoueur
      </Typography>
      
      {/* Quiz Question */}
      <Box width="100%" maxWidth="600px" mb={4}>
        <Typography variant="h5">
          Question: {quizQuestion || "En attente de la question..."}
        </Typography>
        <TextField 
          fullWidth
          label="Votre réponse"
          value={userAnswer}
          onChange={(e) => setUserAnswer(e.target.value)}
          variant="outlined"
          margin="normal"
        />
        <Button variant="contained" color="primary" onClick={handleAnswerSubmit}>
          Soumettre la Réponse
        </Button>
      </Box>

      {/* Scoreboard */}
      <Box width="100%" maxWidth="600px" mb={4}>
        <Typography variant="h6">Tableau de Bord</Typography>
        <List>
          {scoreboard.map((entry, index) => (
            <ListItem key={index}>
              {entry.user}: {entry.score} points
            </ListItem>
          ))}
        </List>
      </Box>

      {/* Chat Room */}
      <Box width="100%" maxWidth="600px" mb={4} p={2} sx={{ border: "1px solid #ddd", borderRadius: "8px" }}>
        <Typography variant="h6">Système de Discussion</Typography>
        <List>
          {chatMessages.map((chat, index) => (
            <ListItem key={index}>
              <Typography variant="body2"><strong>{chat.user}:</strong> {chat.message}</Typography>
              <Divider />
            </ListItem>
          ))}
        </List>
        <TextField
          fullWidth
          placeholder="Envoyer un message..."
          onKeyPress={(e) => {
            if (e.key === "Enter" && e.target.value) {
              sendMessage(e.target.value);
              e.target.value = ""; // Clear input after sending
            }
          }}
          variant="outlined"
        />
      </Box>

      {/* End Quiz Button */}
      <Button 
        variant="contained" 
        color="secondary" 
        onClick={endQuiz} 
        size="large"
      >
        Terminer le Quiz
      </Button>
    </Box>
  );
};

export default Quiz;
