import React, { useState, useEffect, useRef } from "react";
import {
  Card,
  CardContent,
  Typography,
  FormControl,
  Radio,
  RadioGroup,
  FormControlLabel,
  Button,
  Box,
  Alert,
  TextField,
  List,
  ListItem,
} from "@mui/material";
import { useNavigate } from "react-router-dom"; 

const Question = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState("");
  const [selected, setSelected] = useState("");
  const [timer, setTimer] = useState(60);
  const [activeQuestion, setActiveQuestion] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [notifications, setNotifications] = useState([]);

  const ws = useRef(null);
  const navigate = useNavigate(); 

  useEffect(() => {
    const wss = new WebSocket.Server({ port: 3081 });

  
    ws.current.onmessage = (message) => {
      const incomingData = JSON.parse(message.data);
  
      if (incomingData.type === "newQuestion") {
        // Vérifiez les données des questions
        if (
          !incomingData.question ||
          !Array.isArray(incomingData.choices) ||
          incomingData.choices.length < 2
        ) {
          console.error("Données reçues invalides. Requête pour nouvelle question...");
          ws.current.send(JSON.stringify({ type: "requestNewQuestion" }));
        } else {
          setData(incomingData);
          setSelected("");
          setTimer(60);
        }
      } else if (incomingData.type === "endQuiz") {
        console.log("Quiz terminé:", incomingData.message);
      } else if (incomingData.type === "message") {
        setMessages((prev) => [...prev, incomingData]);
      }
    };
  
    return () => {
      if (ws.current) ws.current.close();
    };
  }, []);

  useEffect(() => {
    if (timer > 0) {
      const countdown = setTimeout(() => setTimer(timer - 1), 1000);
      if (timer === 10) {
        setNotifications((prev) => [...prev, "10 seconds remaining!"]);
      }
      return () => clearTimeout(countdown);
    } else {
      setNotifications((prev) => [...prev, "Time's up!"]);
    }
  }, [timer]);

  const handlePrev = () => {
    setError("");
    if (activeQuestion > 0) {
      setActiveQuestion(activeQuestion - 1);
    }
  };

  const handleNext = () => {
    if (selected === "") {
      return setError("Please select one option!");
    }

    const updatedAnswers = [...answers];
    updatedAnswers[activeQuestion] = {
      q: data.question,
      a: selected,
    };
    setAnswers(updatedAnswers);

    setActiveQuestion(activeQuestion + 1);
    setSelected("");
  };

  const handleSubmit = () => {
    if (selected === "") {
      return setError("Please select one option!");
    }

    const updatedAnswers = [...answers];
    updatedAnswers[activeQuestion] = {
      q: data.question,
      a: selected,
    };
    setAnswers(updatedAnswers);

    console.log("Quiz submitted:", updatedAnswers);
    setNotifications((prev) => [...prev, "Quiz submitted successfully!"]);

    navigate("/end", { state: { answers: updatedAnswers, time: timer } });
  };

  const changeHandler = (e) => {
    setSelected(e.target.value);
    if (error) {
      setError("");
    }
  };

  const handleSendMessage = () => {
    if (newMessage) {
      const message = { type: "message", content: newMessage };
      ws.current.send(JSON.stringify(message));
      setNewMessage("");
    }
  };

  return (
    <Card sx={{ maxWidth: 600, margin: "auto", mt: 4, p: 2 }}>
      <CardContent>
        <Box display="flex" justifyContent="space-between" alignItems="center">
          <Typography variant="h6">
            Question {activeQuestion + 1}/{data?.totalQuestions || "?"}
          </Typography>
          <Typography variant="h6">{timer}s</Typography>
        </Box>
        <Typography variant="h5" sx={{ mt: 2 }}>
          {data?.question || "Loading question..."}
        </Typography>

        {error && <Alert severity="error">{error}</Alert>}
        {notifications.map((notification, index) => (
          <Alert key={index} severity="info">
            {notification}
          </Alert>
        ))}

        <FormControl component="fieldset" sx={{ mt: 2 }}>
          <RadioGroup value={selected} onChange={changeHandler}>
            {data?.choices?.map((choice, i) => (
              <FormControlLabel
                key={i}
                value={choice}
                control={<Radio />}
                label={choice}
              />
            ))}
          </RadioGroup>
        </FormControl>
      </CardContent>

      <Box display="flex" justifyContent="space-between" sx={{ p: 2 }}>
        {activeQuestion > 0 && (
          <Button variant="contained" color="secondary" onClick={handlePrev}>
            Prev
          </Button>
        )}
        {activeQuestion + 1 >= data?.totalQuestions ? (
          <Button variant="contained" color="primary" onClick={handleSubmit}>
            Submit
          </Button>
        ) : (
          <Button variant="contained" color="primary" onClick={handleNext}>
            Next
          </Button>
        )}
      </Box>

      {/* Chat Section */}
      <Box sx={{ mt: 4 }}>
        <Typography variant="h6">Chat Room</Typography>
        <List
          sx={{
            maxHeight: 200,
            overflow: "auto",
            backgroundColor: "#f0f0f0",
            p: 1,
          }}
        >
          {messages.map((message, index) => (
            <ListItem key={index}>{message.content}</ListItem>
          ))}
        </List>
        <Box display="flex" mt={2}>
          <TextField
            fullWidth
            variant="outlined"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type a message..."
          />
          <Button variant="contained" color="primary" onClick={handleSendMessage}>
            Send
          </Button>
        </Box>
      </Box>
    </Card>
  );
};

export default Question;
