import React, { useState, useEffect, useRef } from "react";
//import { useSelector, useDispatch } from "react-redux";
import {
  nextQuiz,
  prevQuiz,
  submitQuiz,
  timeOut,
} from "../redux/quizAction";
import {
  Card,
  CardContent,
  Typography,
  FormControl,
  Radio,
  RadioGroup,
  FormControlLabel,
  Button,
  Box,
  Alert,
} from "@mui/material";

const Question = () => {
  const dispatch = useDispatch();
  const { activeQuestion, answers, time } = useSelector(
    (state) => state?.quizReducer
  );

  const [data, setData] = useState(null);
  const [error, setError] = useState("");
  const [selected, setSelected] = useState("");
  const [timer, setTimer] = useState(time);
  const radiosWrapper = useRef();
  const ws = useRef(null);

 
  useEffect(() => {
    ws.current = new WebSocket("ws://localhost:3080");

    ws.current.onmessage = (message) => {
      const incomingData = JSON.parse(message.data);
      if (incomingData.type === "newQuestion") {
        setData(incomingData);
        setSelected(""); 
      }
    };

    return () => {
      if (ws.current) ws.current.close();
    };
  }, []);

  useEffect(() => {
    if (timer > 0) {
      const countdown = setTimeout(() => setTimer(timer - 1), 1000);
      return () => clearTimeout(countdown);
    } else {
      dispatch(timeOut());
    }
  }, [timer, dispatch]);

 
  useEffect(() => {
    if (answers[activeQuestion] != undefined) {
      setSelected(answers[activeQuestion].a);
    }
  }, [activeQuestion, answers]);

  const changeHandler = (e) => {
    setSelected(e.target.value);
    if (error) {
      setError("");
    }
  };

  const handlePrev = () => {
    setError("");
    dispatch(prevQuiz());
  };

  const handleNext = () => {
    if (selected === "") {
      return setError("Please select one option!");
    }
    let ans = [...answers];
    ans[activeQuestion] = {
      q: data.question,
      a: selected,
    };
    dispatch(nextQuiz({ answers: ans }));
    setSelected("");
  };

  const handleSubmit = () => {
    if (selected === "") {
      return setError("Please select one option!");
    }
    dispatch(
      submitQuiz({
        answers: [
          ...answers,
          (answers[activeQuestion] = {
            q: data.question,
            a: selected,
          }),
        ],
        time: time - timer,
      })
    );
  };

  return (
    <Card sx={{ maxWidth: 600, margin: "auto", mt: 4, p: 2 }}>
      <CardContent>
        <Box display="flex" justifyContent="space-between" alignItems="center">
          <Typography variant="h6">
            Question {activeQuestion + 1}/{data?.totalQuestions || "?"}
          </Typography>
          <Typography variant="h6">{timer}s</Typography>
        </Box>
        <Typography variant="h5" sx={{ mt: 2 }}>
          {data?.question || "Loading question..."}
        </Typography>

        {error && <Alert severity="error">{error}</Alert>}

        <FormControl component="fieldset" sx={{ mt: 2 }} ref={radiosWrapper}>
          <RadioGroup value={selected} onChange={changeHandler}>
            {data?.choices?.map((choice, i) => (
              <FormControlLabel
                key={i}
                value={choice}
                control={<Radio />}
                label={choice}
              />
            ))}
          </RadioGroup>
        </FormControl>
      </CardContent>

      <Box display="flex" justifyContent="space-between" sx={{ p: 2 }}>
        {activeQuestion > 0 && (
          <Button variant="contained" color="secondary" onClick={handlePrev}>
            Prev
          </Button>
        )}
        {activeQuestion + 1 >= data?.totalQuestions ? (
          <Button variant="contained" color="primary" onClick={handleSubmit}>
            Submit
          </Button>
        ) : (
          <Button variant="contained" color="primary" onClick={handleNext}>
            Next
          </Button>
        )}
      </Box>
    </Card>
  );
};

export default Question;
