import React, { useState, useEffect, useRef } from "react";
import {
  Card,
  CardContent,
  Typography,
  FormControl,
  Radio,
  RadioGroup,
  FormControlLabel,
  Button,
  Box,
  Alert,
  TextField,
  List,
  ListItem,
} from "@mui/material";

const Question = () => {
  const [data, setData] = useState(null); 
  const [error, setError] = useState("");
  const [selected, setSelected] = useState("");
  const [timer, setTimer] = useState(60);
  const [activeQuestion, setActiveQuestion] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [notifications, setNotifications] = useState([]);

  const ws = useRef(null);

  useEffect(() => {
    ws.current = new WebSocket("ws://localhost:3080");

    ws.current.onmessage = (message) => {
      const incomingData = JSON.parse(message.data);

      switch (incomingData.type) {
        case "newQuestion":
          setData(incomingData.question);
          setSelected("");
          setTimer(60); // Reset timer for each question
          break;
        case "chatMessage":
          setMessages((prev) => [...prev, incomingData.message]);
          break;
        case "notification":
          setNotifications((prev) => [...prev, incomingData.notification]);
          break;
        default:
          console.error("Unknown data type:", incomingData.type);
      }
    };

    return () => {
      if (ws.current) ws.current.close();
    };
  }, []);

  useEffect(() => {
    if (timer > 0) {
      const countdown = setTimeout(() => setTimer(timer - 1), 1000);
      return () => clearTimeout(countdown);
    }
  }, [timer]);

  const handleSendMessage = () => {
    if (ws.current && newMessage) {
      ws.current.send(JSON.stringify({ type: "chatMessage", message: newMessage }));
      setNewMessage("");
    }
  };

  const handleNext = () => {
    if (selected === "") {
      setError("Please select one option!");
      return;
    }
    setAnswers((prev) => [
      ...prev,
      { question: data.question, answer: selected },
    ]);
    setActiveQuestion((prev) => prev + 1);
    setSelected("");
  };

  return (
    <Card sx={{ maxWidth: 600, margin: "auto", mt: 4, p: 2 }}>
      <CardContent>
        <Box display="flex" justifyContent="space-between" alignItems="center">
          <Typography variant="h6">a
            Question {activeQuestion + 1}/{data?.totalQuestions || "?"}
          </Typography>
          <Typography variant="h6">{timer}s</Typography>
        </Box>
        <Typography variant="h5" sx={{ mt: 2 }}>
          {data?.question || "Loading question..."}
        </Typography>
        {error && <Alert severity="error">{error}</Alert>}
        <FormControl component="fieldset" sx={{ mt: 2 }}>
          <RadioGroup value={selected} onChange={(e) => setSelected(e.target.value)}>
            {data?.choices?.map((choice, i) => (
              <FormControlLabel key={i} value={choice} control={<Radio />} label={choice} />
            ))}
          </RadioGroup>
        </FormControl>
      </CardContent>
      <Box display="flex" justifyContent="space-between" sx={{ p: 2 }}>
        {activeQuestion + 1 >= (data?.totalQuestions || 1) ? (
          <Button variant="contained" color="primary" onClick={() => console.log("Quiz submitted")}>
            Submit
          </Button>
        ) : (
          <Button variant="contained" color="primary" onClick={handleNext}>
            Next
          </Button>
        )}
      </Box>

      {/* Chat Section */}
      <CardContent>
        <Typography variant="h6">Chat Room</Typography>
        <List>
          {messages.map((msg, index) => (
            <ListItem key={index}>{msg}</ListItem>
          ))}
        </List>
        <TextField
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          label="Type a message"
          fullWidth
        />
        <Button onClick={handleSendMessage} variant="contained" sx={{ mt: 1 }}>
          Send
        </Button>
      </CardContent>

      {/* Notifications Section */}
      <CardContent>
        <Typography variant="h6">Notifications</Typography>
        <List>
          {notifications.map((notif, index) => (
            <ListItem key={index}>{notif}</ListItem>
          ))}
        </List>
      </CardContent>
    </Card>
  );
};

export default Question;
