import React, { useState, useEffect } from "react";
import { Typography, Button, Box, TextField, List, ListItem, Divider } from "@mui/material";
import { useNavigate } from "react-router-dom";

// Main Quiz Component
const Quiz = () => {
  const navigate = useNavigate();
  const [chatMessages, setChatMessages] = useState([]);
  const [scoreboard, setScoreboard] = useState([]);
  const [quizQuestion, setQuizQuestion] = useState("");
  const [userAnswer, setUserAnswer] = useState("");

  // Sample functions to simulate real-time multiplayer mode and score history
  useEffect(() => {
    // Placeholder: Fetch initial scoreboard and real-time updates.
    // Simulate fetching quiz data, scoreboard, and chat messages.
  }, []);

  const handleAnswerSubmit = () => {
    // Handle answer submission, update scores and move to the next question.
    setScoreboard((prev) => [...prev, { user: "Player 1", score: Math.floor(Math.random() * 100) }]);
    setUserAnswer(""); // Reset answer input
  };

  const endQuiz = () => {
    navigate("/"); // Navigate to home or results page after quiz completion
  };

  const sendMessage = (message) => {
    // Handle chat message send
    setChatMessages((prevMessages) => [...prevMessages, { user: "Player 1", message }]);
  };

  return (
    <Box 
      display="flex" 
      flexDirection="column" 
      alignItems="center" 
      height="100vh" 
      p={3}
      sx={{
        backgroundColor: "#f5f5f5",
        overflowY: "scroll",
      }}
    >
      <Typography variant="h4" gutterBottom>
        Quiz en Mode Multijoueur
      </Typography>
      
      {/* Quiz Question */}
      <Box width="100%" maxWidth="600px" mb={4}>
        <Typography variant="h5">
          Question: {quizQuestion || "Quelle est la capitale de la France?"}
        </Typography>
        <TextField 
          fullWidth
          label="Votre rÃ©ponse"
          value={userAnswer}
          onChange={(e) => setUserAnswer(e.target.value)}
          variant="outlined"
          margin="normal"
        />
        <Button variant="contained" color="primary" onClick={handleAnswerSubmit}>
          Soumettre la RÃ©ponse
        </Button>
      </Box>

      {/* Scoreboard */}
      <Box width="100%" maxWidth="600px" mb={4}>
        <Typography variant="h6">Tableau de Bord</Typography>
        <List>
          {scoreboard.map((entry, index) => (
            <ListItem key={index}>
              {entry.user}: {entry.score} points
            </ListItem>
          ))}
        </List>
      </Box>

      {/* Chat Room */}
      <Box width="100%" maxWidth="600px" mb={4} p={2} sx={{ border: "1px solid #ddd", borderRadius: "8px" }}>
        <Typography variant="h6">SystÃ¨me de Discussion</Typography>
        <List>
          {chatMessages.map((chat, index) => (
            <ListItem key={index}>
              <Typography variant="body2"><strong>{chat.user}:</strong> {chat.message}</Typography>
              <Divider />
            </ListItem>
          ))}
        </List>
        <TextField
          fullWidth
          placeholder="Envoyer un message..."
          onKeyPress={(e) => {
            if (e.key === "Enter" && e.target.value) {
              sendMessage(e.target.value);
              e.target.value = ""; // Clear input after sending
            }
          }}
          variant="outlined"
        />
      </Box>

      {/* End Quiz Button */}
      <Button 
        variant="contained" 
        color="secondary" 
        onClick={endQuiz} 
        size="large"
      >
        Terminer le Quiz
      </Button>
    </Box>
  );
};

export default Quiz;
