import React, { useState, useEffect, useRef } from 'react';
import { Card, CardContent, Typography, FormControl, Radio, RadioGroup, FormControlLabel, Button, Box, Alert, TextField, List, ListItem } from '@mui/material';
import { useNavigate } from 'react-router-dom';

const Question = () => {
  const [question, setQuestion] = useState(null);
  const [selectedAnswer, setSelectedAnswer] = useState('');
  const [error, setError] = useState('');
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [questionIndex, setQuestionIndex] = useState(0); // Track current question index
  
  const ws = useRef(null);
  const navigate = useNavigate();
  
  useEffect(() => {
    // Connect to the WebSocket server
    ws.current = new WebSocket('ws://localhost:3080');

    // Handle incoming messages from the WebSocket server
    ws.current.onmessage = (message) => {
      const data = JSON.parse(message.data);

      if (data.type === 'newQuestion') {
        // Update the question data received from the server
        setQuestion(data);
      } else if (data.type === 'message') {
        // Update the chat messages
        setMessages((prev) => [...prev, data]);
      }
    };

    // Cleanup the WebSocket connection on component unmount
    return () => {
      if (ws.current) ws.current.close();
    };
  }, []);

  const handleNext = () => {
    if (selectedAnswer === '') {
      setError('Please select an answer!');
      return;
    }

    // Send the answer to the WebSocket server
    const answerData = {
      type: 'submitAnswer',
      answer: selectedAnswer,
    };
    ws.current.send(JSON.stringify(answerData));

    // Go to the next question
    setQuestionIndex(prevIndex => prevIndex + 1);
  };

  const handlePrevious = () => {
    // Go to the previous question if not already at the first one
    setQuestionIndex(prevIndex => (prevIndex > 0 ? prevIndex - 1 : 0));
  };

  const handleSubmit = () => {
    if (selectedAnswer === '') {
      setError('Please select an answer!');
      return;
    }

    // Send the answer to the WebSocket server
    const answerData = {
      type: 'submitAnswer',
      answer: selectedAnswer,
    };
    ws.current.send(JSON.stringify(answerData));

    // Navigate to the '/end' page after submitting the answer
    navigate('/end');
  };

  const handleChange = (e) => {
    setSelectedAnswer(e.target.value);
    if (error) {
      setError('');
    }
  };

  const handleSendMessage = () => {
    if (newMessage) {
      const messageData = {
        type: 'message',
        content: newMessage,
      };
      ws.current.send(JSON.stringify(messageData));
      setNewMessage('');
    }
  };

  return (
    <Card sx={{ maxWidth: 600, margin: 'auto', mt: 4, p: 2 }}>
      <CardContent>
        {/* Question Header */}
        <Box display="flex" justifyContent="space-between" alignItems="center">
          <Typography variant="h6">Question {questionIndex + 1}</Typography>
        </Box>
        {question ? (
          <>
            <Typography variant="h5" sx={{ mt: 2 }}>
              {question.question}
            </Typography>

            {/* Display choices */}
            <FormControl component="fieldset" sx={{ mt: 2 }}>
              <RadioGroup value={selectedAnswer} onChange={handleChange}>
                {question.choices.map((choice, i) => (
                  <FormControlLabel
                    key={i}
                    value={choice}
                    control={<Radio />}
                    label={choice}
                  />
                ))}
              </RadioGroup>
            </FormControl>
          </>
        ) : (
          <Typography variant="body1">Loading question...</Typography>
        )}

        {/* Error message */}
        {error && <Alert severity="error">{error}</Alert>}

        {/* Chat Section */}
        <Box sx={{ mt: 4 }}>
          <Typography variant="h6">Chat Room</Typography>
          <List sx={{ maxHeight: 200, overflow: 'auto', backgroundColor: '#f0f0f0', p: 1 }}>
            {messages.map((msg, index) => (
              <ListItem key={index}>{msg.content}</ListItem>
            ))}
          </List>
          <Box display="flex" mt={2}>
            <TextField
              fullWidth
              variant="outlined"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              placeholder="Type a message..."
            />
            <Button variant="contained" color="primary" onClick={handleSendMessage}>
              Send
            </Button>
          </Box>
        </Box>
      </CardContent>

      {/* Navigation and Answer Submission */}
      <Box display="flex" justifyContent="space-between" sx={{ p: 2 }}>
        <Button variant="contained" color="primary" onClick={handlePrevious} disabled={questionIndex === 0}>
          Previous Question
        </Button>
        <Button variant="contained" color="primary" onClick={handleNext}>
          Next Question
        </Button>
        <Button variant="contained" color="secondary" onClick={handleSubmit}>
          Submit Answer
        </Button>
      </Box>
    </Card>
  );
};

export default Question;
