import React from "react";
import { Button, Typography, Box } from "@mui/material";
import { useNavigate } from "react-router-dom";
import Img from "../asset/10740576.jpg";
import Quiz from "./Quiz";

const Home = () => {  // Renommé de 'home' à 'Home'
  const navigate = useNavigate();

  const startQuiz = () => {
    navigate("/quiz");
  };

  return (
    <Box 
     display="flex" 
      justifyContent="center" 
      alignItems="center" 
      height="100vh" 
      flexDirection="column"
      className="home-background"
    >
      <Typography variant="h1" gutterBottom>
        Bienvenue au Quiz!
      </Typography>
      <Button 
        variant="contained" 
        color="primary" 
        onClick={startQuiz} 
        size="large"
      >
        Commencer le Quiz
      </Button>
    </Box>
  );
};

export default Home;  // N'oubliez pas de l'exporter avec la majuscule
