import React, { useState, useEffect } from 'react';
import { Container, Box, Typography, Button, List, ListItem, ListItemText, TextField, Paper } from '@mui/material';

const Quiz = () => {
  const [question, setQuestion] = useState(null);
  const [choices, setChoices] = useState([]);
  const [selectedAnswer, setSelectedAnswer] = useState("");
  const [scoreboard, setScoreboard] = useState([]);
  const [messages, setMessages] = useState([]);
  const [message, setMessage] = useState("");
  const [username] = useState(`User${Math.floor(Math.random() * 1000)}`);
  const [ws, setWs] = useState(null);

  useEffect(() => {
    const socket = new WebSocket("ws://localhost:3080");
    setWs(socket);

    socket.onopen = () => {
      console.log("Connected to WebSocket server");
    };

    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      
      switch (data.type) {
        case "newQuestion":
          setQuestion(data.question);
          setChoices(data.choices);
          setSelectedAnswer("");
          break;
        case "scoreUpdate":
          setScoreboard(data.scoreboard);
          break;
        case "newMessage":
          setMessages((prevMessages) => [...prevMessages, { user: data.user, message: data.message }]);
          break;
        case "endQuiz":
          setQuestion(data.message);
          setChoices([]);
          break;
        default:
          console.log("Unknown message type:", data.type);
      }
    };

    socket.onclose = () => {
      console.log("Disconnected from WebSocket server");
    };

    return () => {
      socket.close();
    };
  }, []);

  const handleAnswerSubmit = () => {
    if (selectedAnswer && ws) {
      ws.send(JSON.stringify({ type: "submitAnswer", user: username, answer: selectedAnswer }));
    }
  };

  const handleChatSubmit = () => {
    if (message && ws) {
      ws.send(JSON.stringify({ type: "chatMessage", user: username, message }));
      setMessage("");
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 4 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        <Typography variant="h5" gutterBottom>
          Quiz Game
        </Typography>
        
        {question && (
          <Box>
            <Typography variant="h6">{question}</Typography>
            <List>
              {choices.map((choice, index) => (
                <ListItem 
                  button 
                  key={index} 
                  selected={selectedAnswer === choice}
                  onClick={() => setSelectedAnswer(choice)}
                >
                  <ListItemText primary={choice} />
                </ListItem>
              ))}
            </List>
            <Button 
              variant="contained" 
              color="primary" 
              onClick={handleAnswerSubmit} 
              sx={{ mt: 2 }}
              disabled={!selectedAnswer}
            >
              Submit Answer
            </Button>
          </Box>
        )}

        <Box sx={{ mt: 4 }}>
          <Typography variant="h6">Scoreboard</Typography>
          <List>
            {scoreboard.map((entry, index) => (
              <ListItem key={index}>
                <ListItemText primary={`${entry.user}: ${entry.score} points`} />
              </ListItem>
            ))}
          </List>
        </Box>

        <Box sx={{ mt: 4 }}>
          <Typography variant="h6">Chat</Typography>
          <List>
            {messages.map((msg, index) => (
              <ListItem key={index}>
                <ListItemText primary={`${msg.user}: ${msg.message}`} />
              </ListItem>
            ))}
          </List>
          <TextField
            label="Type a message"
            variant="outlined"
            fullWidth
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            sx={{ mt: 2 }}
          />
          <Button 
            variant="contained" 
            color="secondary" 
            onClick={handleChatSubmit} 
            sx={{ mt: 2 }}
          >
            Send Message
          </Button>
        </Box>
      </Paper>
    </Container>
  );
};

export default Quiz;
